import React, { useState } from "react";
import { Form, Button, Row, Col, Card } from "react-bootstrap";

const FlightBookingForm = () => {
  const [tripType, setTripType] = useState("one-way");
  const [origin, setOrigin] = useState("");
  const [destination, setDestination] = useState("");
  const [departure, setDeparture] = useState("");
  const [returnDate, setReturnDate] = useState("");

  const handleBooking = (e) => {
    e.preventDefault();
    // Implement booking functionality here
    console.log({ tripType, origin, destination, departure, returnDate });
  };

  return (
    <Card className="p-4">
      <Card.Body>
        <h2 style={{ color: "#ffffff" }}>Book a Flight</h2>
        <Form onSubmit={handleBooking}>
          <Form.Group>
            <Form.Label style={{ color: "#ffffff" }}>Trip Type</Form.Label>
            <Form.Control
              as="select"
              value={tripType}
              onChange={(e) => setTripType(e.target.value)}
            >
              <option value="one-way">One Way</option>
              <option value="return">Return</option>
            </Form.Control>
          </Form.Group>
          <Row>
            <Col>
              <Form.Group>
                <Form.Label style={{ color: "#ffffff" }}>From</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="City or Airport"
                  value={origin}
                  onChange={(e) => setOrigin(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col>
              <Form.Group>
                <Form.Label style={{ color: "#ffffff" }}>To</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="City or Airport"
                  value={destination}
                  onChange={(e) => setDestination(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>
          <Form.Group>
            <Form.Label style={{ color: "#ffffff" }}>Departure Date</Form.Label>
            <Form.Control
              type="date"
              value={departure}
              onChange={(e) => setDeparture(e.target.value)}
            />
          </Form.Group>
          {tripType === "return" && (
            <Form.Group>
              <Form.Label style={{ color: "#ffffff" }}>Return Date</Form.Label>
              <Form.Control
                type="date"
                value={returnDate}
                onChange={(e) => setReturnDate(e.target.value)}
              />
            </Form.Group>
          )}
          <Button variant="primary" type="submit">
            Book Flight
          </Button>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default FlightBookingForm;
