import React, { useState } from "react";
import { Form, Button, Row, Col } from "react-bootstrap";
import Select from "react-select";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const airports = [
  { value: "JFK", label: "John F. Kennedy International Airport (JFK)" },
  { value: "LAX", label: "Los Angeles International Airport (LAX)" },
  // Add more airports here
];

const FlightBookingForm = () => {
  const [tripType, setTripType] = useState("one-way");
  const [origin, setOrigin] = useState("");
  const [destination, setDestination] = useState("");
  const [departure, setDeparture] = useState("");
  const [returnDate, setReturnDate] = useState("");
  const [adults, setAdults] = useState(1);
  const [children, setChildren] = useState(0);
  const navigate = useNavigate();

  const handleBooking = async (e) => {
    e.preventDefault();
    const bookingData = {
      flightNumber: "12345", // Placeholder, ideally generated or fetched
      airline: "Airline Name", // Placeholder
      departure: origin,
      arrival: destination,
      departureTime: departure,
      arrivalTime: returnDate || departure, // If one-way, use departure as arrival
      status: "Booked",
      passengers: { adults, children },
    };

    try {
      const response = await axios.post("/api/flights/book", bookingData);
      navigate("/confirmation", { state: { booking: response.data } });
    } catch (error) {
      console.error("Error booking flight:", error);
    }
  };

  return (
    <div>
      <h2>Book a Flight</h2>
      <Form onSubmit={handleBooking}>
        <Form.Group>
          <Form.Label>Trip Type</Form.Label>
          <Form.Control
            as="select"
            value={tripType}
            onChange={(e) => setTripType(e.target.value)}
          >
            <option value="one-way">One Way</option>
            <option value="return">Return</option>
          </Form.Control>
        </Form.Group>
        <Row>
          <Col>
            <Form.Group>
              <Form.Label>From</Form.Label>
              <Select
                options={airports}
                value={airports.find((airport) => airport.value === origin)}
                onChange={(selectedOption) => setOrigin(selectedOption.value)}
                placeholder="Origin"
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group>
              <Form.Label>To</Form.Label>
              <Select
                options={airports}
                value={airports.find(
                  (airport) => airport.value === destination
                )}
                onChange={(selectedOption) =>
                  setDestination(selectedOption.value)
                }
                placeholder="Destination"
              />
            </Form.Group>
          </Col>
        </Row>
        <Form.Group>
          <Form.Label>Departure Date</Form.Label>
          <Form.Control
            type="date"
            value={departure}
            onChange={(e) => setDeparture(e.target.value)}
          />
        </Form.Group>
        {tripType === "return" && (
          <Form.Group>
            <Form.Label>Return Date</Form.Label>
            <Form.Control
              type="date"
              value={returnDate}
              onChange={(e) => setReturnDate(e.target.value)}
            />
          </Form.Group>
        )}
        <Form.Group>
          <Form.Label>Adults</Form.Label>
          <div>
            <Button
              variant="outline-primary"
              onClick={() => setAdults(adults + 1)}
            >
              +
            </Button>
            <span>{adults}</span>
            <Button
              variant="outline-primary"
              onClick={() => setAdults(adults > 1 ? adults - 1 : 1)}
            >
              -
            </Button>
          </div>
        </Form.Group>
        <Form.Group>
          <Form.Label>Children</Form.Label>
          <div>
            <Button
              variant="outline-primary"
              onClick={() => setChildren(children + 1)}
            >
              +
            </Button>
            <span>{children}</span>
            <Button
              variant="outline-primary"
              onClick={() => setChildren(children > 0 ? children - 1 : 0)}
            >
              -
            </Button>
          </div>
        </Form.Group>
        <Button variant="primary" type="submit">
          Book Flight
        </Button>
      </Form>
    </div>
  );
};

export default FlightBookingForm;
