import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { Card, Button, Alert, Container } from "react-bootstrap";

const Confirmation = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { booking } = location.state || {};

  if (!booking) {
    return (
      <Container className="mt-5">
        <Alert variant="warning">
          No booking information found. Please make a booking first.
        </Alert>
        <Button variant="primary" onClick={() => navigate("/")}>
          Go to Home
        </Button>
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <h1 className="text-center mb-4 text-primary">🎉 Booking Confirmation</h1>

      <Card className="mb-4 shadow">
        <Card.Header className="bg-primary text-white">
          <h4 className="mb-0">✈️ Flight Details</h4>
        </Card.Header>
        <Card.Body>
          <div className="row">
            <div className="col-md-6">
              <p>
                <strong>Flight Number:</strong> {booking.flightNumber}
              </p>
              <p>
                <strong>Airline:</strong> {booking.airline}
              </p>
              <p>
                <strong>Departure:</strong> {booking.departure}
              </p>
              <p>
                <strong>Arrival:</strong> {booking.arrival}
              </p>
            </div>
            <div className="col-md-6">
              <p>
                <strong>Departure Time:</strong>{" "}
                {new Date(booking.departureTime).toLocaleString()}
              </p>
              <p>
                <strong>Arrival Time:</strong>{" "}
                {new Date(booking.arrivalTime).toLocaleString()}
              </p>
              <p>
                <strong>Status:</strong>{" "}
                <span className="text-success fw-bold">{booking.status}</span>
              </p>
              <p>
                <strong>Price:</strong>{" "}
                <span className="text-primary fw-bold">₹{booking.price}</span>
              </p>
            </div>
          </div>
        </Card.Body>
      </Card>

      <Card className="mb-4 shadow">
        <Card.Header className="bg-success text-white">
          <h4 className="mb-0">👤 Passenger Information</h4>
        </Card.Header>
        <Card.Body>
          <p>
            <strong>Name:</strong> {booking.firstName} {booking.lastName}
          </p>
          <p>
            <strong>Email:</strong> {booking.email}
          </p>
          <p>
            <strong>Phone:</strong> {booking.phone}
          </p>
          {booking.age && (
            <p>
              <strong>Age:</strong> {booking.age}
            </p>
          )}
          {booking.gender && (
            <p>
              <strong>Gender:</strong> {booking.gender}
            </p>
          )}
          {booking.paymentMethod && (
            <p>
              <strong>Payment Method:</strong> {booking.paymentMethod}
            </p>
          )}
          {booking.address && (
            <p>
              <strong>Address:</strong> {booking.address}
            </p>
          )}
        </Card.Body>
      </Card>

      <div className="text-center">
        <Alert variant="success" className="mb-4">
          <h5 className="mb-2">✅ Your booking has been confirmed!</h5>
          <p className="mb-0">You will receive a confirmation email shortly.</p>
        </Alert>

        <Button
          variant="primary"
          size="lg"
          onClick={() => navigate("/")}
          className="me-3"
        >
          🏠 Book Another Flight
        </Button>
        <Button
          variant="outline-secondary"
          size="lg"
          onClick={() => navigate("/profile")}
        >
          👤 View Profile
        </Button>
      </div>
    </Container>
  );
};

export default Confirmation;
